example.synopsis:
source s_apache {
    channel {
        source { file("/var/log/apache/error.log"); };
        parser(p_apache_parser);
    };
};

log { source(s_apache); ... };

note.synopsis:
parser demo-parser() {
    channel {
        junction {
            channel { ... };
            channel { ... };
        };
    };
};

source s_filtered_hosts {
  channel{
    source {
      pipe("/dev/pipe");
      syslog(ip(192.168.0.1) transport("tcp"));
      syslog(ip(127.0.0.1) transport("tcp"));
    };
    filter {
      netmask(10.0.0.0/16);
    };
  };
};;
