section.synopsis:
parser parser_name {
    grouping-by(
        key()
        having()
        aggregate()
        timeout()
    );
};

example.synopsis:
parser auditd_groupingby {
    grouping-by(
        key(".auditd.msg")
        aggregate(
            value("MESSAGE" "$(format-json .auditd.*)")
            )
        timeout(10)
    );
};
