example.synopsis:
source s_auditd {
    file(/var/log/audit/audit.log flags(no-parse));
};

destination d_json {
    file("/tmp/test.json"
        template("$(format-json .auditd.*)\n"));
};

parser p_auditd {
    linux-audit-parser (prefix(".auditd."));
};

log {
    source(s_auditd);
    parser(p_auditd);
    destination(d_json);
};

source s_auditd {
    file(/var/log/audit/audit.log);
};

destination d_json {
    file("/tmp/test.json"
        template("$(format-json .auditd.*)\n"));
};

log {
    source(s_auditd);
    parser {
        linux-audit-parser (prefix(".auditd."));
    };
    destination(d_json);
};
